export const addFav = (character) => {
 return async function(dispatch) {
    try { 
        const response = await axios.post("http://localhost:3001/rickandmorty/fav", character);
        return dispatch({
             type: ADD_FAVORITE,
             payload: response.data,
             });
    } catch (error) { 
        // console.log(error); o
    }
 }
};
// export const delFav = (id)=> {
